import './App.css';
import { useState, useEffect } from "react";
import createTheme from '@mui/material/styles/createTheme';
import ThemeProvider from '@mui/material/styles/ThemeProvider';
import Button from '@mui/material/Button';
import Checkbox from '@mui/material/Checkbox';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormGroup from '@mui/material/FormGroup';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Axios from "axios";

// theme for MUI
const muiTheme = createTheme({
  palette: {
    primary: {
      main: '#1363DF'
    },
    secondary: {
      main: '#47B5FF'
    }
  }
});

function App() {
  const [listOfUsers, setListOfUsers] = useState([]); // the list of our users 

  // each variable in the User object 
  const [name, setName] = useState("");
  const [age, setAge] = useState(0);
  const [frequentStudier, setFrequentStudier] = useState(false);
  const [extroverted, setExtroverted] = useState(false); 
  const [neat, setNeat] = useState(false);

  // variables for searching for relevant roommates in the table 
  const [searchFrequentStudier, setSearchFrequentStudier] = useState(false);
  const [searchExtroverted, setSearchExtroverted] = useState(false);
  const [searchNeat, setSearchNeat] = useState(false);

  useEffect(() => {
    Axios.get("http://localhost:3001/getUsers").then((response) => {
      setListOfUsers(response.data.sort(compatibilitySort));
    });
  }, [listOfUsers]);

  // Function that calls the createUser api endpoint with the data inputted from the user 
  const createUser = () => {
    Axios.post("http://localhost:3001/createUser", {
      name, 
      age, 
      frequentStudier,
      extroverted,
      neat
    }).then((response) => {
      setListOfUsers([
        ...listOfUsers, 
        {
          name, 
          age, 
          frequentStudier,
          extroverted,
          neat
        },
      ].sort(compatibilitySort));
    });
  }

  // compares two user objects to the search requirements
  function compatibilitySort(a, b) {

    let numKeys = Object.keys(a).length - 2; 
    let aComparisonValue = 0.0;
    let bComparisonValue = 0.0; 

    // User A similarities
    if (a.frequentStudier === searchFrequentStudier) {
      aComparisonValue = aComparisonValue + (1.00 / numKeys);
    }

    if (a.extroverted === searchExtroverted) {
      aComparisonValue = aComparisonValue + (1.00 / numKeys);
    }

    if (a.neat === searchNeat) {
      aComparisonValue = aComparisonValue + (1.00 / numKeys);
    }

    // User B similariies 
    if (b.frequentStudier === searchFrequentStudier) {
      bComparisonValue = bComparisonValue + (1.00 / numKeys);
    }

    if (b.extroverted === searchExtroverted) {
      bComparisonValue = bComparisonValue + (1.00 / numKeys);
    }

    if (b.neat === searchNeat) {
      bComparisonValue = bComparisonValue + (1.00 / numKeys);
    }

    if (aComparisonValue > bComparisonValue) {
      return -1; 
    }
    return 1; 
  }


  return (
    <ThemeProvider theme = {muiTheme}>
      <div className="App">

        <div className="Title">
          <h1>RoommatesNEU</h1>
          <h2>A roommate search engine</h2>
        </div>

        <div className="SearchCriteria">
          <h3>Enter your preferences below:</h3>

          <FormGroup>
            <FormControlLabel control={<Checkbox onChange={(event) => { setSearchFrequentStudier(event.target.checked); }}/>} label="I am looking for a roommate who tends to study frequently" />
            <FormControlLabel control={<Checkbox onChange={(event) => { setSearchExtroverted(event.target.checked); }}/>} label="I am looking for a roommate who is generally outgoing" />
            <FormControlLabel control={<Checkbox onChange={(event) => { setSearchNeat(event.target.checked); }}/>} label="I am looking for a roommate who is a neat person" />
          </FormGroup>

          <br></br>
        </div>

        <div className="UsersDisplay">
          <TableContainer component={Paper}>
            <Table sx={{ width: 1200 }} aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>Name</TableCell>
                  <TableCell align="right">Age</TableCell>
                  <TableCell align="right">Frequent Studier</TableCell>
                  <TableCell align="right">Extroverted</TableCell>
                  <TableCell align="right">Neat</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {listOfUsers.map((user) => (
                  <TableRow
                    key={user.name}
                    sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                  >
                    <TableCell component="th" scope="row">
                      {user.name}
                    </TableCell>
                    <TableCell align="right">{user.age.toString()}</TableCell>
                    <TableCell align="right">{user.frequentStudier.toString()}</TableCell>
                    <TableCell align="right">{user.extroverted.toString()}</TableCell>
                    <TableCell align="right">{user.neat.toString()}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </div>

        <div>
          <h3>
            Post yourself in our system!<br></br>
            Fill out the questionnaire below!
          </h3>
        </div>

        <div>
          <FormGroup>
            <TextField style ={{width: '15%', scale: '10%'}} id="outlined-basic" label="Name" variant="outlined" onChange={(event) => { setName(event.target.value); }}/>
            <TextField style ={{width: '15%', scale: '10%'}} id="outlined-basic" label="Age" type="number" variant="outlined" onChange={(event) => { setAge(event.target.value); }}/>
            <FormControlLabel control={<Checkbox onChange={(event) => { setFrequentStudier(event.target.checked); }}/>} label="I tend to study frequently" />
            <FormControlLabel control={<Checkbox onChange={(event) => { setExtroverted(event.target.checked); }}/>} label="I am generally an outgoing person" />
            <FormControlLabel control={<Checkbox onChange={(event) => { setNeat(event.target.checked); }}/>} label="I consider myself a neat person" />
          </FormGroup>

          <Button onClick={createUser} color="primary" variant="contained" >
            Create User
          </Button>
        </div>
      </div>
    </ThemeProvider>
  );
}

export default App;
